import React from 'react';
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
// AVATAR IMPORTS
// import avatar1 from "../images/avatars/avatar-1.png";
import avatar2 from "../images/avatars/avatar-2.png";
import avatar3 from "../images/avatars/avatar-3.png";
import avatar4 from "../images/avatars/avatar-4.png";


const TestimonialsCarousel = () => {
    return (
        <Carousel
        showArrows={true}
        infiniteLoop={true}
        showThumbs={false}
        showStatus={false}
        autoPlay={true}
        interval={7000}
        >
            <>
            <img src="https://media-exp1.licdn.com/dms/image/C4E03AQEGzogFScsa5w/profile-displayphoto-shrink_800_800/0/1554162300050?e=1621468800&v=beta&t=zH6DDK22KZuwb8gQl6iSawjEfKLL84URSRFINuZqa7o" alt="John Doe 1"/>
            <div className="myCarousel">
            <h3>Gregory Mash</h3>
            <p>Jennifer is extremely devoted to her work. She always puts in extra time and effort on her assignments to put them above and beyond requirements. She has both a great work ethic and a positive attitude. She was fantastic with her communication and comprehension skills throughout class. All of her coding assignments and projects throughout the bootcamp were of the highest quality. I am sure she will be excellent in any role she pursues in the future.</p>
            </div>
            </>
            <>
            <img src={avatar2} alt="Jane Doe 2"/>
            <div className="myCarousel">
            <h3>Jane Doe 2</h3>
            <p>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Sit hic, vel impedit sapiente animi illo quo, ducimus iste delectus fuga id obcaecati sint ad dolorem optio. Dignissimos non hic magnam.</p>
            </div>
            </>
            <>
            <img src={avatar3} alt="John Doe 3"/>
            <div className="myCarousel">
            <h3>John Doe 3</h3>
            <p>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Sit hic, vel impedit sapiente animi illo quo, ducimus iste delectus fuga id obcaecati sint ad dolorem optio. Dignissimos non hic magnam.</p>
            </div>
            </>
            <>
            <img src={avatar4} alt="John Doe 4"/>
            <div className="myCarousel">
            <h3>John Doe 4</h3>
            <p>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Sit hic, vel impedit sapiente animi illo quo, ducimus iste delectus fuga id obcaecati sint ad dolorem optio. Dignissimos non hic magnam.</p>
            </div>
            </>
        </Carousel>
    )
}

export default TestimonialsCarousel
